                          {houseTutorValues.map((element, index) => (
                            <div
                              className="d-flex justify-content-center align-items-center gap-1"
                              key={index}
                            >
                              <div class="col-md-3 mb-3">
                                <input
                                  type="text"
                                  class="form-control"
                                  placeholder="House Tutor Name"
                                  name="housetutor_name"
                                  onChange={(e) => housetutorChange(index, e)}
                                  value={element.caretaker_name || ""}
                                />
                              </div>
                              <div class="col-md-3 mb-3">
                                <input
                                  type="text"
                                  class="form-control"
                                  placeholder="NID/BC"
                                  name="nid "
                                  onChange={(e) => housetutorChange(index, e)}
                                  value={element.nid || ""}
                                />
                              </div>
                              <div class="col-md-3 mb-3">
                                <input
                                  type="text"
                                  class="form-control"
                                  placeholder="Mobile No"
                                  name="mobile"
                                  onChange={(e) => housetutorChange(index, e)}
                                  value={element.mobile || ""}
                                />
                              </div>
                              <div class="col-md-3 mb-3">
                                <input
                                  type="text"
                                  class="form-control"
                                  placeholder="Area"
                                  name="area"
                                  onChange={(e) => housetutorChange(index, e)}
                                  value={element.area || ""}
                                />
                              </div>

                              {index ? (
                                <div className="">
                                  <span
                                    type="button"
                                    class="badge bg-danger px-1"
                                    onClick={() => removeCaretakerFields(index)}
                                  >
                                    x
                                  </span>
                                </div>
                              ) : null}
                            </div>
                          ))}

                          <div className="  d-flex justify-content-end mb-3   ">
                            <div className="   ">
                              <button
                                type="button"
                                class="btn btn-secondary btn-sm"
                                onClick={() => addHouseTutorFields()}
                              >
                                Add Another One
                              </button>
                            </div>
                          </div>
                        </form>

                        {/* //// Flats Details  */}
                        <h5 className="text-start">Flats Details</h5>

                        <form>
                          {flateValues.map((element, index) => (
                            <div
                              className="d-flex justify-content-center align-items-center gap-1"
                              key={index}
                            >
                              <div class="col-md-4 mb-3">
                                <input
                                  type="text"
                                  class="form-control"
                                  placeholder="FlateName"
                                  name="flat_name"
                                  onChange={(e) => flateChange(index, e)}
                                  value={element.flat_name || ""}
                                />
                              </div>
                              <div class="col-md-4 mb-3">
                                <input
                                  type="text"
                                  class="form-control"
                                  placeholder="Flate Renter Name"
                                  name="flat_renter_name"
                                  onChange={(e) => flateChange(index, e)}
                                  value={element.flat_renter_name || ""}
                                />
                              </div>
                              <div class="col-md-4 mb-3">
                                <input
                                  type="text"
                                  class="form-control"
                                  placeholder="Refferel Code"
                                  name="referral_code"
                                  onChange={(e) => flateChange(index, e)}
                                  value={element.referral_code || ""}
                                />
                              </div>

                              {index ? (
                                <div className="">
                                  <span
                                    type="button"
                                    class="badge bg-danger px-1"
                                    onClick={() => removeFlateFields(index)}
                                  >
                                    x
                                  </span>
                                </div>
                              ) : null}
                            </div>
                          ))}

                          <div className="  d-flex justify-content-end mb-3   ">
                            <button
                              type="button"
                              class="btn btn-secondary btn-sm"
                              onClick={() => addFlateFields()}
                            >
                              Add Another One
                            </button>
                          </div>
                        </form>

                        <div class="col-md-6 mb-3">
                          <input
                            type="text"
                            class="form-control"
                            placeholder=" Date(D-M-Y)"
                            onChange={(e) => handleChangeTwo(e)}
                            name="land_lord_form_submit_date"
                            value={formData.land_lord_form_submit_date}
                          />
                        </div>
                        <div class="col-md-6 mb-3">
                          <input
                            type="text"
                            class="form-control"
                            placeholder="Signature of Rent Person"
                            onChange={(e) => handleChangeTwo(e)}
                            name="land_lord_signature"
                            value={formData.land_lord_signature}
                          />
                        </div>

                        <div className="  d-flex justify-content-end mb-3  ">
                          <input
                            type="submit"
                            class="btn btn-secondary btn-sm  "
                            value="Save & Continue"
                          />
                        </div>
                      </div>
                    </div>
                  </div>
                </form>
              </div>
            </div>
          </section>
        </main>
      </div>
    </div>
  );
};

export default AddLandLord;
