                            <div class="col-md-4  ">
                              <div class="col-sm-10">
                                <select
                                  class="form-select"
                                  aria-label="Default select example"
                                  name="m_district"
                                  id="district"
                                  for="divisions"
                                >
                                  <option disabled selected>
                                    Select District
                                  </option>
                                  {districts.map((district) => (
                                    <option
                                      key={district.id}
                                      value={district.id}
                                      district={district}
                                    >
                                      {district.name}
                                    </option>
                                  ))}
                                </select>
                              </div>
                            </div>

                            <div class="col-md-4   ">
